mapper.update:
UPDATE user
SET id=#{userDTO.id}, sex=#{userDTO.sex}, age=#{userDTO.age}, phone=#{userDTO.phone}, email=#{userDTO.email}, name=#{userDTO.name}
WHERE id=#{id}

mapper.select:
SELECT id
FROM user
WHERE id=#{id}

SELECT id
FROM user
WHERE name=#{name} and phone=#{phone} and email=#{email}

SELECT pw
FROM user
WHERE id=#{id} and name=#{name}

mapper.update:
UPDATE user
SET pw=#{pw}
WHERE id=#{id}

UPDATE user
SET pw=#{pw}
WHERE id=#{id}
